import React from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';

import Header from './components/Header';

import AboutPage from './pages/AboutPage';
import HomePage from './pages/HomePage';
import NfcPage from './pages/NfcPage';
import QuestPage from './pages/QuestPage';

import './App.css';

export default class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      headerLinks: [
        { name: "home", path: "/" },
        { name: "about", path: "/about" },
        { name: "nfc", path: "/nfc" },
        { name: "quests", path: "/quests" }
      ],
      home: {
        title: "Welcome to HoloTag World!",
        subtitle: "Coming Summer 2020",
        description: "Do you love adventure? Are you an avid explorer? If the answer to either of those questions is yes, get ready for an exciting new way to explore the world around you!"
      },
      about: {
        title: "What is HoloTag World?"
      },
      nfc: {
        title: "NFC Technology",
        summary: "We use NFC technology to transform your device into a portal to your next adventure."
      },
      quests: {
        title: "Quest System",
        summary: "The platform is based on a questing system. Find a tag, start a quest. Buy a tag, create a quest."
      }
    }
  }

  render() {
    return (
      <Router>
        <Container fluid="true">

          <Navbar className="bg-transparent">
            <Navbar.Brand>
              <Link to="/">HoloTag World</Link>
            </Navbar.Brand>

            <Navbar.Toggle className="border-0" aria-controls="navbar-toggle" />
            <Navbar.Collapse id="navbar-toggle">

              <Nav className="ml-auto">
                <Link className="nav-link" to="/">Home</Link>
                <Link className="nav-link" to="/about">About</Link>
                {/* <Link className="nav-link" to="/blog/posts">Blog</Link> */}
                <Link className="nav-link" to="/quests">Quests</Link>

              </Nav>

            </Navbar.Collapse>
          </Navbar>

          <Route path="/" render={() => {
            <HomePage title={this.state.home.title} subtitle={this.state.home.subtitle} decsription={this.state.home.description} />
          }} />


        </Container>
      </Router>
    );
  }
}
